import React, { Component, useState } from 'react'

import '../App.css';

function SearchBar(props) {

const [Query, setQuery] = useState('');
const [Images, setImages] = useState({});

async function getImages ( query ) {
  let response = await fetch(`https://pixabay.com/api/?key=17564121-870a14721f28b8246a1c9583d&q=${query}`);
  let result = await response.json()
  console.warn (result);
  setImage(result.hits);
  return result;
}

const renderImages = () => {
	const {images} = this.state;
	if (Object.keys(images).length && images.length) {
		return (
			<div className="results-container">
				{images.map((result) => {
					return (
						<div className="image-wrapper">
						<img className="image" src={result.webformatURL} alt={result.user}/>
				</div>
					);
				})}
			</div>
		);
	}
};

const inputChange = (event) => {
	const query = event.target.value;
    setQuery( { query:query } );
