};

return (
    <div>
      <label htmlFor="search-input" className="search-label"> 
      <input 
        type="text"
        value= {query}
        name="query"
        id="search-input"
        placeholder="search image..." 
        onChange={inputChange} />
        <i className="fa fa-search search-icon"/>
      </label>
      { renderImages() }
    </div>
  )
}

export default SearchBar;
