import React, { Component } from 'react'
import '../App.css';

class SearchBar extends Component {
constructor ( props ) {
  super( props );
  this.state = {
    query: '',
    images: {},
  }
}

async getImages ( query ) {
  let response = await fetch(`https://pixabay.com/api/?key=17564121-870a14721f28b8246a1c9583d&q=${query}`);
  let result = await response.json()
  console.warn (result);
  this.setState({
    images: result.hits,
  });
  return result;
}

inputChange = (event) => {
	const query = event.target.value;
    this.setState( { query:query } );
    this.getImages (this.query) ;
};

renderImages = () => {
	const {images} = this.state;
	if (Object.keys(images).length && images.length) {
		return (
			<div className="results-container">
				{images.map((result) => {
					return (
						<div className="image-wrapper">
						<img className="image" src={result.webformatURL} alt={result.user}/>
				</div>
					);
				})}
			</div>
		);
	}
};


render() {
  const { query } = this.state;
  return (
    <div>
      <label htmlFor="search-input" className="search-label"> 
      <input 
        type="text"
        value= {query}
        name="query"
        id="search-input"
        placeholder="search image..." 
        onChange={this.inputChange} />
        <i className="fa fa-search search-icon"/>
      </label>
      { this.renderImages() }
    </div>
    )
  }
}

export default SearchBar;
